/**
 * NextAuth.js Type Extensions
 * 
 * Extends NextAuth types to include custom session properties
 */

import "next-auth"
import "next-auth/jwt"

declare module "next-auth" {
  interface Session {
    user: {
      id: string
      name?: string | null
      email?: string | null
      image?: string | null
      githubUsername?: string // Custom property for GitHub username
    }
  }

  interface User {
    id: string
    name?: string | null
    email?: string | null
    image?: string | null
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    id: string
    githubUsername?: string
  }
}

